<?phpnamespace Atlantisgroupbv\Model\Connection;use Psr\Log\LoggerInterface;/** * Class AbstractConnectionInterface * @package Atlantisgroupbv\Model\Connection */abstract class AbstractConnectionFactory extends MySql implements ConnectionFactoryInterface{    /**     * @param string $host     * @param string $user     * @param string $pass     * @param array $dbname     * @param string $port     * @param string $charset     * @param LoggerInterface $logger     */    public function __construct(string $host, string $user, string $pass, array $dbname, string $port, string $charset, LoggerInterface $logger)    {        parent::__construct($host, $user, $pass, $dbname, $port, $charset, $logger);    }}