<?phpnamespace Atlantisgroupbv\Model\Connection;use Atlantisgroupbv\Model\DataAccess\DataAccessInterface;use Psr\Log\LoggerInterface;abstract class AbstractSql implements DataAccessInterface{    /**     * @var ConnectionFactoryInterface     */    protected ConnectionFactoryInterface $connectionFactory;    /**     * @var bool     */    protected bool $debug;    /**     * @var LoggerInterface|null     */    protected ?LoggerInterface $logger;    /**     * @param ConnectionFactoryInterface $connectionFactory     * @param bool $debug     */    public function __construct(ConnectionFactoryInterface $connectionFactory, bool $debug = false)    {        $this->connectionFactory = $connectionFactory;        $this->debug = $debug;    }    /**     * @param LoggerInterface|null $logger     * @return DataAccessInterface     */    public function setLogger(LoggerInterface $logger = null): DataAccessInterface    {        $this->logger = $logger;        return $this;    }}